mapper.select:
SELECT 
	row_number() over (ORDER BY updatetime DESC) as xh,
	,
	(select array_to_json(array_agg(row_to_json(t)))
	from
	(select * from t_enclosure de where xid = de.xid) t
	) enclosure
	from 
	where 1=1


		and xid = #{pd.xid}



		and uid = #{pd.uid}



		and operation_type = #{pd.operation_type}



		and job_type = #{pd.job_type}



		and state = #{pd.state}




		and system_id = #{pd.system_id}



		and updatetime >= concat(#{pd.beginTime},' 00:00:00')::TIMESTAMP


		and updatetime <= concat(#{pd.endTime},' 23:59:59')::TIMESTAMP

	and visiable = 1

SELECT 

	from  as n
	where 1=1 
	and n.visiable = 1
	and n.xid = #{xid}
	and n.uid = #{uid}
